    <div className="background">
      {/* <Navbar2 /> */}
      
      <a href="/" className="navlogo">
        <div data-aos="fade-right" className="fade">
          <img className="dinologo" src={dinoLogo} />
        </div>
      </a>

      <div className="links2">
        <a href="/SignUp">Sign Up</a>
        <a href="/LogIn">Sign In</a>
        <a href="/Profile">Profile</a>
      </div>

      <div className="profile">
        <div data-aos="zoom-in-up" className="fade">
          <div className="justify-content-md-center">
            <Form>
              <div data-aos="fade-up" className="fade">
                <div className="text-center">
                  
                </div>
                <Container>
                  <Row>
                    <Col>
                      <Form.Group className="mb-3" controlId="Rname">
                        <Form.Label>Change Restaurant Name</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) => setRestaurantName(e.target.value)}
                          placeholder="Enter Name"
                          id="Rname"
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group className="mb-3" >
                        <Form.Label>Change Location</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) =>
                            setRestaurantLocation(e.target.value)
                          }
                          placeholder="Enter Location"
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group className="mb-3" >
                        <Form.Label>Cuisine (comma seperated)</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) => setCuisine(e.target.value)}
                          placeholder="Enter Cuisine"
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group className="mb-3">
                        <Form.Label>City</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) => setRestaurantCity(e.target.value)}
                          placeholder="Enter City"
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group className="mb-3" >
                        <Form.Label>Street</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) => setRestaurantStreet(e.target.value)}
                          placeholder="Enter Street No"
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group className="mb-3" >
                        <Form.Label>House</Form.Label>
                        <Form.Control
                          type="text"
                          onChange={(e) => setRestaurantHouse(e.target.value)}
                          placeholder="Enter House No"
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group className="mb-3">
                        <Form.Label>Current Password</Form.Label>
                        <Form.Control
                          type="password"
                          id="pass1"
                          onChange={(e) => setpassword(e.target.value)}
                          placeholder="Enter Password"
                        />
                      </Form.Group>
                    </Col>
                    <Col>
                      <Form.Group
                        className="mb-3"
              
                      >
                        <Form.Label>New Password </Form.Label>
                        <Form.Control
                          type="password"
                          id="pass2"
                          onChange={(e) => setcpassword(e.target.value)}
                          placeholder="Enter Confirm Password"
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                </Container>
                <Button id="emnei" variant="primary" type="submit" value="Submit" onClick={handleSubmit}>
                  Submit
                </Button>
                <br />
              </div>
            </Form>
          </div>
        </div>
      </div>
    </div>
  );

};

export default EditRestaurantProfile;
