const RestaurantLogIn = () => {
  useEffect(() => {
      Aos.init({ duration: 1500 });
  }, []);
  const [email, setEmail] = useState("");
    const [password, setpass] = useState("");
    const handleSubmit= async (event)=>{
       
        event.preventDefault();
        const user ={
            email,
            password
        }
        
        try {
            //CREATE BACKEND
          const response = await axios.post('/post/login', user);
          console.log(response);
          if(response.data.status=="ok" && response.data.role=="restaurant"){
            alert("login successful");
            localStorage.setItem('token', response.data.user);
           window.location.href = "/dash";
          }
          else if(response.data.status=="ok" && response.data.role!="restaurant"){
            alert("wrong password");
          }
          else{
            alert("user does not exist");
          }
        } catch (error) {
            console.log(error);
        }
    }
    return (
      <Container>
          <Row>
              <Col md={6} sm={12}>
                  <div data-aos="fade-right" className="fade">
                      <div className="left">
                          <img className="bg" src={img} />
                      </div>
                  </div>
              </Col>
              <Col md={6} sm={12}>
                  <div className="justify-content-md-center">
                      <Form>
                          <div data-aos="fade-up" className="fade">
                              <div className="text-center">
                                  <img className="user" src={userIcon} />
                              </div>
                              <Form.Group className="mb-3" controlId="formBasicEmail">
                                  <Form.Control type="email" onChange={e=>setEmail(e.target.value)} placeholder="Enter email" name="email" id="email" value={user.email} onChange={handleInputs}/>
                              </Form.Group>
                              <Form.Group className="mb-3" controlId="formBasicPassword">
                                  <Form.Control type="password" onChange={e=>setpass(e.target.value)} placeholder="Password" name="pass" id="pass" value={user.pass} onChange={handleInputs}/>
                              </Form.Group>
                              <Form.Group className="mb-3" controlId="formBasicCheckbox">
                                  <Form.Check type="checkbox" label="Remember me" />
                              </Form.Group>
                              <div className="mb-3">
                                  <a href="#">Forgot password?</a>
                              </div>
                              <Button type="submit" value="Submit" onClick={handleSubmit}variant="primary">
                                  Submit
                              </Button>
                              <br/>
                              <p className="emnei">Or sign in using</p>
                              {/* <i class="bi bi-facebook"></i> */}
